import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './format/UserProfile.css';

const ProfilePage = () => {
  const [userData, setUserData] = useState({});
  const [error, setError] = useState('');

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    const token = localStorage.getItem('token');

    try {
      const response = await axios.get(`http://localhost:5000/api/user/profile`, {
        headers: { 'x-access-token': `${token}` }
      });

      setUserData(response.data);
    } catch (error) {
      console.error(error);
      setError('Error. Please log in again.');
    }
  };

  const handleDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/user/delete-account',
        { password },
        { headers: { 'x-access-token': token } }
      );
      if (response.data.success) {
        // Account deleted successfully, log out the user
        localStorage.removeItem('token');
        // Redirect to login or home page
        window.location.href = '/login'; // or any other route
      } else {
        setDeleteError('Failed to delete account. Please try again.');
      }
    } catch (error) {
      console.error(error);
      setDeleteError('Failed to delete account. Please try again.');
    }
  };

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      <div className="profile-card">
        {error && <p className="error-message">{error}</p>}
        <p>Username: {userData.username}</p>
        <p>Email: {userData.email}</p>

        <Link to="/edit-profile">
          <button>Edit Profile</button>
        </Link>
        <button className="delete-button" onClick={() => setShowConfirmation(true)}>
          Delete Account
        </button>

        {showConfirmation && (
          <div className="confirmation-modal">
            <p>Are you sure you want to delete your account?</p>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleDelete}>Confirm Delete</button>
            {deleteError && <p className="error-message">{deleteError}</p>}
            <button onClick={() => setShowConfirmation(false)}>Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
