import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EditProfile.css';

const EditProfile = () => {
  const [formData, setFormData] = useState({
    newUsername: '',
    newEmail: '',
    newPassword: ''
  });
  const [message, setMessage] = useState('');
  const [currentUserData, setCurrentUserData] = useState(null);

  // Fetch user data when component mounts
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/user'); // Assuming this is your endpoint to fetch user data
        setCurrentUserData(res.data); // Assuming response contains user data
      } catch (error) {
        console.error(error);
      }
    };

    fetchUserData();
  }, []);

  // Update form data when current user data changes
  useEffect(() => {
    if (currentUserData) {
      setFormData({
        newUsername: currentUserData.username,
        newEmail: currentUserData.email,
        newPassword: ''
      });
    }
  }, [currentUserData]);

  const { newUsername, newEmail, newPassword } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/edit-profile', formData);
      setMessage('Profile updated successfully!');
      console.log(res.data);
    } catch (error) {
      console.error(error.response.data);
      setMessage('Failed to update profile. Please try again.');
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Edit Profile</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="newUsername"
          value={newUsername}
          onChange={handleChange}
          placeholder={currentUserData ? currentUserData.username : 'Username'}
        />
        <input
          type="email"
          name="newEmail"
          value={newEmail}
          onChange={handleChange}
          placeholder={currentUserData ? currentUserData.email : 'Email'}
        />
        <input
          type="password"
          name="newPassword"
          value={newPassword}
          onChange={handleChange}
          placeholder="New Password"
        />
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default EditProfile;
