import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Modal, Button } from 'react-bootstrap'; // Import Bootstrap components for modal

import './format/EditProfile.css';

const EditProfile = () => {
  const [formData, setFormData] = useState({
    newUsername: '',
    newEmail: '',
    newPassword: ''
  });
  const [message, setMessage] = useState('');
  const [userData, setUserData] = useState({});
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [password, setPassword] = useState('');
  const [usernameUnique, setUsernameUnique] = useState(true); // State to track username uniqueness
  const [emailUnique, setEmailUnique] = useState(true); // State to track email uniqueness

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    const token = localStorage.getItem('token');

    try {
      const response = await axios.get(`http://localhost:5000/api/user/profile`, {
        headers: { 'x-access-token': `${token}` }
      });

      setUserData(response.data);
      setFormData({
        newUsername: response.data.username || '', // Set placeholder to user's current username
        newEmail: response.data.email || '', // Set placeholder to user's current email
        newPassword: ''
      });
    } catch (error) {
      console.error(error);
      setError('Error. Please log in again.');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setUsernameUnique(true); // Reset username uniqueness when user edits the form
    setEmailUnique(true); // Reset email uniqueness when user edits the form
    setError(''); // Clear error message when user edits the form
  };

  const handleCheckUniqueness = async () => {
    try {
      const { newUsername, newEmail } = formData;
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/user/verify-unique', { newUsername, newEmail }, {
        headers: {
          'x-access-token': token
        }
      });
      setUsernameUnique(true); // Username is unique
      setEmailUnique(true); // Email is unique
      return true; // Both username and email are unique
    } catch (error) {
      const errorMessage = error.response.data.message;
      if (errorMessage.includes('Username')) {
        setUsernameUnique(false); // Username is not unique
      }
      if (errorMessage.includes('Email')) {
        setEmailUnique(false); // Email is not unique
      }
      return false; // Either username or email is not unique
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const isUnique = await handleCheckUniqueness();
    if (!isUnique) {
      return; // If either username or email is not unique, do not proceed with form submission
    }
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No token found. Please log in again.');
        return;
      }

      const res = await axios.post('http://localhost:5000/api/user/edit-profile', formData, {
        headers: {
          'x-access-token': token
        }
      });

      setMessage('Profile updated successfully!');
      console.log(res.data);
    } catch (error) {
      console.error(error.response.data);
      if (error.response.data.includes('Username already existed')) {
        setModalMessage('Username is already taken. Please choose a different one.');
        setShowModal(true);
      } else if (error.response.data.includes('Email already existed')) {
        setModalMessage('Email is already taken. Please choose a different one.');
        setShowModal(true);
      } else {
        setError('Failed to update profile. Please try again.');
      }
    }
  };

  const handleModalSubmit = async () => {
    try {
      // Here you can add code to verify password before submitting the form
      setShowModal(false);
      // Optionally, you can also clear the form data or perform other actions after successful password verification
    } catch (error) {
      console.error(error);
      setModalMessage('Error verifying password. Please try again.');
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div className="edit-profile-container">
      <h2>Edit Profile</h2>
      {message && <p>{message}</p>}
      {error && <p>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="newUsername"
          value={formData.newUsername}
          onChange={handleChange}
          placeholder="New Username"
        />
        {!usernameUnique && <p style={{ color: 'red' }}>Username is already taken. Please choose a different one.</p>}
        <input
          type="email"
          name="newEmail"
          value={formData.newEmail}
          onChange={handleChange}
          placeholder="New Email"
        />
        {!emailUnique && <p style={{ color: 'red' }}>Email is already taken. Please choose a different one.</p>}
        <input
          type="password"
          name="newPassword"
          value={formData.newPassword}
          onChange={handleChange}
          placeholder="New Password"
        />
        <button type="submit">Update Profile</button>
      </form>
      {/* Modal pop up for error messages */}
      <Modal show={showModal} onHide={handleCloseModal} backdrop="static" keyboard={true}>
        <Modal.Header closeButton>
          <Modal.Title>Error</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>{modalMessage}</p>
          {/* Additional input for password verification */}
          <input
            type="password"
            placeholder="Verify password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>Cancel</Button>
          <Button onClick={handleModalSubmit}>Submit</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default EditProfile;
