import React, { useState } from 'react';
import axios from 'axios';
import './format/CreateNewTask.css';

const NewTaskForm = ({ userId }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    assignedTo: '',
    dueDate: '',
    priority: ''
  });

  const { title, description, assignedTo, dueDate, priority } = formData;

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/createTask', { ...formData, createdBy: userId });
      console.log(res.data);
      setFormData({
        title: '',
        description: '',
        assignedTo: '',
        dueDate: '',
        priority: ''
      });
      // Here you can handle any UI updates or notifications for successful task creation
    } catch (error) {
      console.error(error.response.data);
      // Here you can handle error messages or display notifications for failed task creation
    }
  };

  return (
    <div className="task-form-container">
      <h2>Create New Task</h2>
      <form onSubmit={handleSubmit} className="task-form">
        <input type="text" name="title" value={title} onChange={handleChange} placeholder="Title" required />
        <textarea name="description" value={description} onChange={handleChange} placeholder="Description" required />
        <input type="text" name="assignedTo" value={assignedTo} onChange={handleChange} placeholder="Assign To" />
        <input type="date" name="dueDate" value={dueDate} onChange={handleChange} placeholder="Due Date" required />
        <select name="priority" value={priority} onChange={handleChange}>
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <button type="submit">Create Task</button>
      </form>
    </div>
  );
};

export default NewTaskForm;
