import React, { useState, useEffect } from 'react';
import NavigationBar from './NavigationBar';
import { Link } from 'react-router-dom';
import axios from 'axios';

function CreateAccount() {
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [reenterPass, setReenterPass] = useState('');
    const [dob, setDob] = useState('');
    const [gender, setGender] = useState('');
    const [error, setError] = useState('');
    const [passwordCriteria, setPasswordCriteria] = useState({
        length: false,
        lowercase: false,
        uppercase: false,
        numeric: false,
        consecutive: false,
    });
    const [showPassword, setShowPassword] = useState(false);
    const [showReenterPassword, setShowReenterPassword] = useState(false);
    const [allFieldsSatisfied, setAllFieldsSatisfied] = useState(false);

    // Style objects
    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '15px',
        marginTop: '50px',
    };

    const inputStyle = {
        width: '100%',
        padding: '15px',
        borderRadius: '5px',
        border: '1px solid #ccc',
        fontSize: '16px',
    };

    const buttonStyle = {
        width: '100%',
        padding: '15px 20px',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '18px',
    };

    const errorStyle = {
        color: 'red',
        marginBottom: '20px',
    };

    const checkPasswordStrength = (password) => {
        const criteria = {
            length: password.length >= 8 && password.length <= 32,
            lowercase: /[a-z]/.test(password),
            uppercase: /[A-Z]/.test(password),
            numeric: /[0-9]/.test(password),
            consecutive: !/(.)\1\1\1/.test(password),
        };

        setPasswordCriteria(criteria);
        const allFieldsSatisfied = Object.values(criteria).every(value => value);
        setAllFieldsSatisfied(allFieldsSatisfied);
    };

    const handleEmailChange = (e) => {
        const { value } = e.target;
        setEmail(value);
        setError('');
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(value)) {
            setError('Invalid email format!');
        }
    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setPasswordCriteria({
            length: false,
            lowercase: false,
            uppercase: false,
            numeric: false,
            consecutive: false,
        });



        try {

            if (password !== reenterPass) {
                setError('Passwords do not match!');
                return;
            }

            await axios.post('http://localhost:3001/api/auth/register', {
                email,
                username,
                password,
                dob,
                gender,
            });
            alert('Account created successfully');
            setEmail('');
            setUsername('');
            setPassword('');
            setDob('');
            setGender('');
            window.location.href = '/login'; 
        } catch (err) {
            console.error(err);
            let errorMessage;
            if (err === "Passwords do not match!") {
                errorMessage = err;
            } else if (err.response && err.response.data && err.response.data.message) {
                // Use the server's error message
                errorMessage = err.response.data.message;
            } else {
                // Use a generic error message
                errorMessage = 'An error occurred during account creation.';
            }
            setError(errorMessage);
        }
    };

    return (
        <div>
            <NavigationBar />
            <div style={{ width: '100%', display: 'flex', justifyContent: 'center' }}>
                <form onSubmit={handleSubmit} style={formStyle}>
                    <h2>Create Account</h2>
                    {error && <p style={errorStyle}>{error}</p>}
                    <input
                        style={inputStyle}
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={handleEmailChange} // Updated to use handleEmailChange
                    />
                    <input
                        style={inputStyle}
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <div style={{ position: 'relative', width: '100%' }}>
                        <input
                            style={{ ...inputStyle, paddingRight: '50px' }}
                            type={showPassword ? 'text' : 'password'}
                            placeholder="Password"
                            value={password}
                            onChange={(e) => {
                                setPassword(e.target.value);
                                checkPasswordStrength(e.target.value);
                            }}
                        />
                        <span
                            style={{
                                position: 'absolute',
                                top: '50%',
                                right: '10px',
                                transform: 'translateY(-50%)',
                                cursor: 'pointer',
                            }}
                            onClick={() => setShowPassword(!showPassword)}
                        >
                            {showPassword ? 'Hide' : 'Show'}
                        </span>
                    </div>
                    {Object.values(passwordCriteria).some(value => !value) &&
                        <div style={{ width: '100%', fontSize: '14px' }}>
                            <ul style={{ listStyleType: 'none', padding: '0', margin: '0' }}>
                                <li style={{ color: passwordCriteria.length ? 'green' : 'red' }}>Length between 8 and 32 characters</li>
                                <li style={{ color: passwordCriteria.lowercase ? 'green' : 'red' }}>At least 1 lowercase letter</li>
                                <li style={{ color: passwordCriteria.uppercase ? 'green' : 'red' }}>At least 1 uppercase letter</li>
                                <li style={{ color: passwordCriteria.numeric ? 'green' : 'red' }}>At least 1 numeric character</li>
                                <li style={{ color: passwordCriteria.consecutive ? 'green' : 'red' }}>No more than 3 identical consecutive characters</li>
                            </ul>
                        </div>
                    }
                    <div style={{ position: 'relative', width: '100%' }}>
                        <input
                            style={{ ...inputStyle, paddingRight: '50px' }}
                            type={showReenterPassword ? 'text' : 'password'}
                            placeholder="Re-enter Password"
                            value={reenterPass}
                            onChange={(e) => setReenterPass(e.target.value)}
                        />
                        <span
                            style={{
                                position: 'absolute',
                                top: '50%',
                                right: '10px',
                                transform: 'translateY(-50%)',
                                cursor: 'pointer',
                            }}
                            onClick={() => setShowReenterPassword(!showReenterPassword)}
                        >
                            {showReenterPassword ? 'Hide' : 'Show'}   
                        </span>
                    </div>
                    
                    {/* Date of Birth Input */}

                    <input
                        style={inputStyle}
                        type="date"
                        placeholder="Date of Birth"
                        value={dob}
                        onChange={(e) => setDob(e.target.value)}
                    />
                    {/* Gender select input */}

                    <select
                        style={inputStyle}
                        value={gender}
                        onChange={(e) => setGender(e.target.value)}
                    >
                        <option value="">Select Gender</option>
                        <option value="male">Male</option>
                        <option value="female">Female</option>
                        <option value="other">Other</option>
                    </select>
                    <button style={buttonStyle} type="submit" disabled={!allFieldsSatisfied}>Create Account</button>
                    <p>
                        <Link to="/login">Back to login</Link>.
                    </p>
                    <p>
                        Forgot your password? <Link to="/passwordreset">Reset your password</Link>.
                    </p>
                </form>
            </div>
        </div>
    );
}

export default CreateAccount;
