import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './format/CreateNewTask.css';

const CreateNewTask = ({ userId }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    assignedTo: [],
    dueDate: '',
    priority: 'Medium'
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const { title, description, assignedTo, dueDate, priority } = formData;

  useEffect(() => {
    const fetchSearchResults = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/user/search/${searchTerm}`);
        setSearchResults(res.data.filter(user => !assignedTo.includes(user._id)));
      } catch (error) {
        console.error(error.response.data);
      }
    };

    if (searchTerm.trim() !== '') {
      fetchSearchResults();
    } else {
      setSearchResults([]);
    }
  }, [searchTerm, assignedTo]);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSearchChange = e => {
    setSearchTerm(e.target.value);
  };

  const handleAssigneeSelect = user => {
    // Check if the user is already assigned
    if (!assignedTo.find(u => u._id === user._id)) {
      setFormData({ ...formData, assignedTo: [...assignedTo, user] });
    }
    setSearchTerm('');
    setSearchResults([]);
  };
  

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:3001/api/tasks', { ...formData, createdBy: userId });
      console.log(res.data);
      setFormData({
        title: '',
        description: '',
        assignedTo: [],
        dueDate: '',
        priority: 'Medium'
      });
      // Here you can handle any UI updates or notifications for successful task creation
    } catch (error) {
      console.error(error.response.data);
      // Here you can handle error messages or display notifications for failed task creation
    }
  };

  return (
    <div className="task-form-container">
      <h2>Create New Task</h2>
      <form onSubmit={handleSubmit} className="task-form">
        <input type="text" name="title" value={title} onChange={handleChange} placeholder="Title" required />
        <textarea name="description" value={description} onChange={handleChange} placeholder="Description" required />
        <div className="assignee-search">
          <input
            type="text"
            value={searchTerm}
            onChange={handleSearchChange}
            placeholder="Search Assignee"
            className="assignee-input"
          />
          <div className="search-results">
            {searchResults.map(user => (
              <button key={user._id} onClick={() => handleAssigneeSelect(user)}>{user.username}</button>
            ))}
          </div>
        </div>
        {assignedTo.length > 0 && (
          <div>
            <p>Assignees:</p>
            <ul>
              {assignedTo.map(user => (
                <li key={user._id}>{user.username}</li>
              ))}
            </ul>
          </div>
        )}
        <input type="date" name="dueDate" value={dueDate} onChange={handleChange} placeholder="Due Date" required />
        <select name="priority" value={priority} onChange={handleChange}>
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <button type="submit">Create Task</button>
      </form>
    </div>
  );
};

export default CreateNewTask;
