import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Modal from 'react-bootstrap/Modal';

import './format/UserProfile.css';

const ProfilePage = () => {
  const [userData, setUserData] = useState({});
  const [error, setError] = useState('');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [password, setPassword] = useState('');
  const [deleteError, setDeleteError] = useState('');
  const [message, setMessage] = useState('');


  const [createNewTaskModal, setNewTaskModal] = useState('');
  const [showNewTaskModal, setShowNewTaskModal] = useState('');
  const [newTask, setNewTask] = useState({ title: '', description: '', asignee: '', dueDate: '' });



  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    const token = localStorage.getItem('token');

    try {
      const response = await axios.get(`http://localhost:5000/api/user/profile`, {
        headers: { 'x-access-token': `${token}` }
      });

      setUserData(response.data);
    } catch (error) {
      console.error(error);
      setError('Error. Please log in again.');
    }
  };

  const handleDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/user/delete-account',
        { password },
        { headers: { 'x-access-token': token } }
      );
      if (response.data.success) {
        // Account deleted successfully, log out the user
        localStorage.removeItem('token');
        // Redirect to login or home page
        window.location.href = '/login'; // or any other route
      } else {
        setDeleteError('Failed to delete account. Please try again.');
      }
    } catch (error) {
      console.error(error);
      setDeleteError('Failed to delete account. Please try again.');
    }
  };

  const handleCloseDeleteModal = () => {
    setShowDeleteModal(false);
    setPassword('');
    setDeleteError('');
  };

  const handleCloseNewTaskModal = () => {
    setShowNewTaskModal(false);
    setN('');
    setDeleteError('');
  };

  const handleCreateTask = async e => {
    e.preventDefault();
    try {
      setNewTask({ ...newTask, createdBy: userData._id });
      console.log(newTask);

      const res = await axios.post(
        'http://localhost:5000/api/task/createTask',
        formData,
        {
          headers: { 'x-access-token': token }
        }
      );

      setMessage('Task created successfully');
      console.log('Response Data:', res.data);

      setNewTask({
        title: '',
        description: '',
        assignedTo: [],
        dueDate: '',
        priority: 'Medium'
      });
    } catch (error) {
      console.error(error.response.data);
      setMessage('Error creating task');
    }
  };



  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      <div className="profile-card">
        {deleteError && <p className="error-message">{deleteError}</p>}
        <p>Username: {userData.username}</p>
        <p>Email: {userData.email}</p>

        <Link to="/edit-profile">
          <button>Edit Profile</button>
        </Link>
        <button className="delete-button" 
          onClick={() => setShowDeleteModal(true)}>Delete Account
        </button>

        {/* <Link to="/create-new-task">
          <button className='delete-button'>
              Create Task
          </button>
        </Link> */}
        <button className="delete-button" 
          onClick={() => setShowNewTaskModal(true)}>Create New Task
        </button>
       
      </div>

      {/* Delete Account Modal */}
      <Modal show={showDeleteModal} onHide={handleCloseDeleteModal} backdrop="static" keyboard={true}>
        <Modal.Header closeButton>
          <Modal.Title>Are you sure?</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Verify your password:</p>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
          />
          {deleteError && <p className="error-message">{deleteError}</p>}
        </Modal.Body>
        <Modal.Footer>
          <button onClick={handleCloseDeleteModal}>Cancel</button>
          <button onClick={handleDelete}>Confirm Delete</button>
        </Modal.Footer>
      </Modal>


      <Modal show={showNewTaskModal} onHide={handleCloseNewTaskModal} backdrop="static" keyboard={true}>
        <Modal.Header closeButton>
          <Modal.Title> Create New Task </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form className='task-from' onSubmit={handleCreateTask}>
            <div className='form-group'>
              <label>Title</label>
              <input type="text" value={newTask.title} onChange={(e) => setNewTask({ ...newTask, title: e.target.value })} required/>
            </div>

            <div classname='form-group'>
              <label>Discription</label>
              <input type="text" value={newTask.description} onChange={(e) => setNewTask({ ...newTask, description: e.target.value})} required/>
            </div>

            <div className="form-group">
              <label>Due Date</label>
              <input type="datetime-local" value={newTask.dueDate} onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })} required />
            </div>
          </form>
        </Modal.Body>
      </Modal>
    </div>
  );
};


export default ProfilePage;
