import React, { useEffect, useState } from 'react';
import axios from 'axios';

import './format/EditProfile.css';

const EditProfile = () => {
  const [formData, setFormData] = useState({
    newUsername: '',
    newEmail: '',
    newPassword: ''
  });
  const [message, setMessage] = useState('');
  const [userData, setUserData] = useState({});
  const [error, setError] = useState('');

//   useEffect(() => {
//     fetchUserData();
//   }, []);

//   const fetchUserData = async () => {
//     const token = localStorage.getItem('token');

//     try {
//       const response = await axios.get(`http://localhost:5000/api/user/profile`, {
//         headers: { 'x-access-token': `${token}` }
//       });

//       setUserData(response.data);
//       setFormData({
//         newUsername: response.data.username || '', // Set placeholder to user's current username
//         newEmail: response.data.email || '', // Set placeholder to user's current email
//         newPassword: ''
//       });
//     } catch (error) {
//       console.error(error);
//       setError('Error. Please log in again.');
//     }
//   };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/edit-profile', formData);
      setMessage('Profile updated successfully!');
      console.log(res.data);
    } catch (error) {
      console.error(error.response.data);
      setError('Failed to update profile. Please try again.');
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Edit Profile</h2>
      {message && <p>{message}</p>}
      {error && <p>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="newUsername"
          value={formData.newUsername}
          onChange={handleChange}
          placeholder="New Username"
        />
        <input
          type="email"
          name="newEmail"
          value={formData.newEmail}
          onChange={handleChange}
          placeholder="New Email"
        />
        <input
          type="password"
          name="newPassword"
          value={formData.newPassword}
          onChange={handleChange}
          placeholder="New Password"
        />
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default EditProfile;
