import React, { useState } from 'react';
import axios from 'axios';
import './format/EditProfile.css';

const EditProfile = () => {
  const [formData, setFormData] = useState({
    newUsername: '',
    newEmail: '',
    newPassword: ''
  });
  const [message, setMessage] = useState('');

  const { newUsername, newEmail, newPassword } = formData;

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    const token = localStorage.getItem('token');
    console.log('Token:', token); // Check the token
  
    try {
      const response = await axios.get('http://localhost:3001/api/user/profile', {
        headers: { 'x-access-token': `${token}` }
      });
  
      setUserData(response.data);
      if (response.data.bio) {
        setMyBio(response.data.bio);
      }
      if (response.data.settings.parentalControl) {
        setPControls({...pControls,
                     [response.data.settings.parentalControl]: true});
      }
    } catch (error) {
      console.error(error);
      setError('Error. Please log in again.'); // Set the error message
    }
  };


  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/edit-profile', formData);
      setMessage('Profile updated successfully!');
      console.log(res.data);
    } catch (error) {
      console.error(error.response.data);
      setMessage('Failed to update profile. Please try again.');
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Edit Profile</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="newUsername"
          value={newUsername}
          onChange={handleChange}
          placeholder="New Username"
        />
        <input
          type="email"
          name="newEmail"
          value={newEmail}
          onChange={handleChange}
          placeholder="New Email"
        />
        <input
          type="password"
          name="newPassword"
          value={newPassword}
          onChange={handleChange}
          placeholder="New Password"
        />
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default EditProfile;
